import React from "react";
import "./App.css";
import { Link } from "react-router-dom";

class Cart extends React.Component {
  state = {};
  render() {
    const { cartItems, list, addToCart, removeFromCart } = this.props;
    const itemMap = list.reduce((map, item) => {
      map[item.id] = item;
      return map;
    }, {});
    const totalItems = Object.values(cartItems).reduce((x, y) => x + y, 0);
    console.log(cartItems);
    console.log(itemMap);
    return (
      <div className="">
        <Link to="/">Item List</Link>
        <div>{totalItems} items added in Cart</div>
        <div>Order Summary</div>
        <div>
          {Object.keys(cartItems).map(itemId => {
            const item = itemMap[itemId];
            const quantity = cartItems[itemId];
            const totalPrice =
              item.price * (1 - item.discount / 100) * quantity;
            return (
              <div key={itemId}>
                <div>Name: {item.name}</div>
                <div>Quantity: {quantity}</div>
                <div>Price: {totalPrice}</div>
                <button onClick={() => removeFromCart(itemId)}>Remove</button>
                <button onClick={() => addToCart(itemId)}>Add</button>
              </div>
            );
          })}
          <div>total </div>
          <label>item({totalItems})</label> : <p></p>
        </div>
      </div>
    );
  }
}

export default Cart;
