import React from "react";
import "./App.css";
import Home from "./Home";
import Cart from "./Cart";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

class App extends React.Component {
  state = { cartItems: [], itemList: [] };
  addToCart = id => this.setState({ cartItems: [...this.state.cartItems, id] });
  removeFromCart = id =>
    this.setState({ cartItems: this.state.cartItems.filter(_id => _id != id) });
  componentDidMount() {
    fetch(`https://api.myjson.com/bins/qhnfp`)
      .then(response => response.json())
      .then(list =>
        this.setState({
          itemList: list.map(item => {
            item.img_url = "https://source.unsplash.com/random/500x500";
            return item;
          })
        })
      );
  }
  render() {
    const { removeFromCart, addToCart } = this;
    const { itemList, cartItems } = this.state;
    return (
      <Router>
        <div className="App">
          <Switch>
            <Route
              render={() => (
                <Home
                  addToCart={addToCart}
                  removeFromCart={removeFromCart}
                  list={itemList}
                />
              )}
            />
            <Route
              render={() => (
                <Cart
                  addToCart={addToCart}
                  removeFromCart={removeFromCart}
                  list={itemList}
                  cartItems={cartItems}
                />
              )}
            />
          </Switch>
        </div>
      </Router>
    );
  }
}

export default App;
