import React from "react";
import "./App.css";
import Home from "./Home";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
class App extends React.Component {
  state = { cartItems: [], itemList: [] };
  addToCart = id => this.setState({ cartItems: [...this.state.cartItems, id] });
  componentDidMount() {
    fetch(`https://api.myjson.com/bins/qhnfp`)
      .then(response => response.json())
      .then(list =>
        this.setState({
          itemList: list.map(item => {
            item.img_url = "https://source.unsplash.com/random/500x500";
            return item;
          })
        })
      );
  }
  render() {
    return (
      <Router>
        <div className="App">
          <Switch>
            {/* <Route
              render={() => (
                <Home addToCart={this.addToCart} list={this.state.itemList} />
              )}
            /> */}
            <Route
              component={Home}
              addToCart={this.addToCart}
              list={this.state.itemList}
            />
          </Switch>
        </div>
      </Router>
    );
  }
}

export default App;
